	package com.ypy.proxy;	   import java.lang.reflect.Method;		public class TankTimeProxy implements com.ypy.proxy.test.UserMgr{	       com.ypy.proxy.InvocationHandler h;			public TankTimeProxy(InvocationHandler h) {				this.h = h;			}			@Override			public void addUser() {				try {				    Method md = com.ypy.proxy.test.UserMgr.class.getMethod("addUser");				    h.invoke(this, md);				} catch (Exception e) {e.printStackTrace();}			}		}